mapper.update:
update board set
title = #{title}, content = #{content}, mod_at = now()
where bno = #{bno}

mapper.select:
select a.bno, title, writer, mod_at, has_file, cmt_qty
from (
	select bno from board
	where bno > 0

	order by bno desc
	limit #{pageStart}, #{qty}
) a left join board b
on a.bno = b.bno
